package com.example.restservice;

import java.io.IOException;
import java.util.Arrays;
import java.util.Date;


import com.launchdarkly.client.LDClient;
import com.launchdarkly.client.LDUser;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;



@RestController
public class TagController {


	@GetMapping("/tag")
	public CFDI tag(@RequestParam(value = "invoiceId") String invoiceId) throws IOException {


		LDClient ldClient = new LDClient("sdk-d3e4544e-0461-4340-86a2-77d6f70fbc06");
		LDUser user = new LDUser.Builder("UNIQUE IDENTIFIER")
				.firstName("Bob")
				.lastName("Loblaw")
				.customString("groups", Arrays.asList("beta_testers"))
				.build();

		boolean showFeature = ldClient.boolVariation("test-1", user, false);


		ldClient.close();

		if (showFeature) {
			return new CFDI( "123", "456", "Test", "Sello de prueba", "Sello CFDI", new Date(), "qrCode");

		} else {
			return new CFDI( "||1.1|8d844a26-f4ee-4b45-bf77-ad0b3cf16ee3|2018-08-29T13:38:15|AAA010101AAA||20001000000300022323||",
					"20001000000300022323",
					"Success",
					"",
					"",
					new Date(),
					"");
		}

	}
}
